INSERT INTO `subapartados` (`id_subapartado`, `id_apartado`, `subapartado`, `recopilador`, `presentacion`, `temporal`, `posicion`) VALUES
(1, 1, 'Ficha General', '<h3><strong>Ubicacion:</strong></h3><p>snell * Canal vertebral 3/4 superiores<br />waxman * 2/3 superiores canal vertebral adultos<br />&nbsp;</p><h3><strong>Forma:</strong></h3><p>*Casi Cilindrica - Aplanamiento anteroporterior<br />&nbsp;</p><h3><strong>Dimensiones:</strong></h3><p>*42cm - 45cm. Diametro varia de longitud<br />*Alargamiento se detiene 4/5 a&ntilde;o<br />wax * Parte inicial -&gt; Inferior al Bulbo raquideo<br />wax * Parte terminal -&gt; Cono medular.<br />wax * tercer mes de vida fetal, el canal vertebral y la medula son del mismo tama&ntilde;o<br />wax * al nacer la medula se extiende hasta L3</p><h3><strong>Limites:</strong></h3><p>Snell * Foramen magno del hueso occipital<br />*Adultos: &quot;&quot; - L1/L2<br />*Neonatos: &quot;&quot; - L3/L4</p><h3><strong>Medios de Fijacion:</strong></h3><h3><strong>Relaciones:</strong></h3><h3><strong>Medios de Proteccion:</strong></h3><p>&nbsp;</p>', '', '', 10),
(3, 1, 'Funciones', '<p><br />*Respuestas Reflejas Rapidas&nbsp;<br />*Via de comunicacion Encefalo - Cuerpo<br />Snell * Cause de informacion Encefalo - Cuerpo<br />*100 millones de neuronas</p><p>&nbsp;</p>', '', '', 100),
(4, 1, 'Estructuras de protecciÃ³n - Columna', '', '', '', 30),
(5, 1, 'Estructuras de ProtecciÃ³n - Meninges', '<h3>Cavidad Epidural&nbsp;</h3><p>*Grasa y tejido conectivo&nbsp;</p><h3>Duramadre</h3><p>*Gruesa y dura&nbsp;<br />*Tejido conectivo denso irregular&nbsp;<br />*<strong>Continuidad:&nbsp;</strong>Agujero Magno del hueso occipital - S2.&nbsp;<br /><strong>*Continuidad:&nbsp;</strong>Con el epineuro</p><h3>Espacio Subdural</h3><p>*liquido intersticial</p><h3>Aracnoides</h3><p>*Delgada y avascular&nbsp;<br />*Delgadas fibras colagenas laxas y fibras elasticas</p><h3>Espacio Subaracnoideo</h3><p>*Contiene LCF</p><h3>Piamadre&nbsp;</h3><p>*Fina y transparente<br />*Celulas pavimentosas cuboides<br />*Vascularizada</p><h3>Liamentos Dentados</h3><p>*Engrosamientos de la piamadre<br />*Entre raices anteriores y posteriores<br />*Medio&nbsp;de fijacion</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p>', '', '<p><span class=\"marker\">Idea de Material: Hacer un cuadro comparativo de las diferentes capas de las meninges. Celulas, tipo de proteinas, caracteristicas estructurales. Extension. Vascularizacion. etc</span></p>', 40),
(6, 1, 'Aspectos AnatÃ³micos Particulares', '<p><br /><strong>Intumescencia Cervical</strong></p><p>*C4 - T1 -&gt; Plexo Braquial</p><p><strong>Intumescencia Lumbar&nbsp;</strong></p><p>*T9 - T12&nbsp;-&gt; Plexo lumbosacro<br />wax * Se afina y forma el cono medular<br />wax * Las intumescencias presentan un alto numero de NMI</p><p><strong>Cono medular&nbsp;</strong></p><p>*Termina en L1/L2<br />Snell * Desciende de aqui el filum terminale</p><p><strong>Filum Terminale</strong></p><p><strong>*</strong>Prolongacion de la piamadre - Extension ??&nbsp;<br />*Se fija al coxis&nbsp;<br />Wax * Se adhiere al saco dural distal<br />Wax * Piamadre, fibras gliales, con frecuencia una vena</p><p><strong>Cola de Caballo</strong></p><p><strong>Fisura media anterior&nbsp;</strong></p><p>wax * Pliegue de pia y vasos sanguineos</p><p><strong>Surco medio posterior</strong></p><p><strong>Surco posterolateral</strong></p><p><strong>Surco anterolateral</strong></p>', '', '<p>Articulo donde encontre la imagen de la medula -&gt;&nbsp;http://www.elsevier.es/es-revista-revista-espanola-medicina-legal-285-articulo-autopsia-del-raquis-S0377473209700161</p><p><img src=\"http://www.elsevier.es/ficheros/publicaciones/03774732/0000003500000002/v1_201305061035/S0377473209700161/v1_201305061035/es/main.assets/gr5.jpeg\" style=\"width:500px\" /></p><p>&nbsp;</p><p>&nbsp;</p><p><img src=\"https://image.slidesharecdn.com/tomas-englermedula4704/95/tomas-englermedula-5-728.jpg?cb=1181201162\" /></p><p><img src=\"http://userscontent2.emaze.com/images/d63b5223-9ed0-4b92-b14d-f635c2fbd656/Slide20_Pic1_636313400065688467.png\" /></p><p>&nbsp;</p>', 20),
(7, 1, 'Nervios y RaÃ­ces', '<h3>Nervios y Raices</h3><p>*Raices: Unen cada nervio con un segmento medular.&nbsp;<br />*Raicillas&nbsp;<br />*Raiz Posterior<br />*Ganglio de la raiz posterior<br />*Raiz Ventral</p><p>wax * El primer segmento carece de raiz dorsal.<br />wax * Cada raiz esta formada por 1 a 8 raicillas.<br />wax * El nervio sale a travez del foramen intervertrebal.<br />wax * El nervio sale aprox entre las respectivas vertebras en relacion con el numero del segmento medular.&nbsp;</p><p>wax * Raiz ventral: Neuronas motoras alfa y gamma y fibras autonomas preganglionares.<br />wax * Raiz dorsal: Via sensitiva.</p><p><br />&nbsp;</p><p>&nbsp;</p>', '', '', 60),
(8, 1, 'DivisiÃ³n SegmentarÃ­a de la MÃ©dula Espinal', '<h3>Divisi&oacute;n Segmentar&iacute;a de la M&eacute;dula Espinal</h3><p>tort *Cada par emerge a intervalos regulares&nbsp;de un segmento espinal a traves de los foramenes intervertrebales</p><p>wax * Cada segmento de la medula a niveles inferiores se localiza por encima del cuerpo vertebral con el mismo numero.&nbsp;</p><p><strong>Segmento Med&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Cuerpo vertebral&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong><br />C8&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; C6 -&nbsp;C7<br />T6&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;T3 - T4<br />T12&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;T9<br />L5&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;T11<br />S&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;T12 - L1</p><p><strong>Division General</strong></p><p>tort *8.S Cervicales&nbsp; &nbsp; &nbsp; &nbsp; 12.S Toracicos&nbsp; &nbsp; &nbsp; &nbsp;5.S Lumbares<br />&nbsp; &nbsp; &nbsp; &nbsp; 5.S Sacros&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;1.S&nbsp; Coccigeo</p>', '', '', 50),
(9, 2, 'Neuronas', '<p>Articulo so</p>', '', '<p>Articulo sobre remielinizacion&nbsp;<br />http://www.elsevier.es/es-revista-neurologia-295-articulo-vitamina-d-remielinizacion-esclerosis-multiple-S0213485316300652</p>', 0),
(10, 1, 'Aspectos Clinicos', '<p>Waxman -&gt; pp 43</p><p>*Anancefalia -&gt; Incompatible con la vida</p><p><img src=\"https://embarazoactual.com/wp-content/uploads/2015/04/anencefalia.gif\" /></p><p><br />*Espina Bifida -&gt; Cierre caudal inadecuado del tubo neural</p><p><img src=\"https://columnavertebral.net/wp-content/uploads/2016/05/espina-bifida.jpg\" /></p><p>&nbsp;</p><p>* Meningocele y Mielomeningocele</p><p><img src=\"https://www.sciencephoto.com/image/778126/530wm/C0305733-Spina_Bifida%2C_Illustration-SPL.jpg\" /></p><p>&nbsp;</p>', '', '', 200),
(11, 1, 'Funciones Motoras y reflejos medulares', '<p><strong>Organizacion de la medula espinal para las funciones motoras</strong></p><p>* Fibra sensitiva -&gt; Motoneruona<br />* Fibra sensitiva -&gt; Interneurona -&gt; Subir o bajar etc&nbsp;<br />* Muchas maneras de formar circuitos</p><p><strong>Motoneuronas anteriores&nbsp;</strong></p><p>&nbsp;</p><p>&nbsp;</p>', '', '', 130),
